SELECT city AS Şehir, country AS Ülke FROM city LEFT JOIN country ON city.country_id = country.country_id;
SELECT payment_id AS "Payment id", first_name AS "İsim", last_name AS "Soy isim" FROM payment RIGHT JOIN customer ON customer.customer_id = payment.customer_id;
SELECT rental_id AS "Rental id", first_name AS "İsim", last_name AS "Soy isim" FROM customer FULL JOIN rental ON customer.customer_id = rental.customer_id;